"use client"

import type React from "react"
import { useState, useEffect } from "react"
import { useParams, useNavigate } from "react-router-dom"
import { getDocumentById, getDocumentsByTag } from "../data/documentsData"
import { useAuth } from "../contexts/AuthContext"
import "./DocumentDetailPage.css"

const DocumentDetailPage: React.FC = () => {
    const { id } = useParams<{ id: string }>()
    const navigate = useNavigate()
    const { user } = useAuth()
    const [document, setDocument] = useState<any>(null)
    const [relatedDocuments, setRelatedDocuments] = useState<any[]>([])
    const [isFavorite, setIsFavorite] = useState(false)
    const [isLoading, setIsLoading] = useState(true)
    const [activeTab, setActiveTab] = useState("content")

    useEffect(() => {
        if (id) {
            const doc = getDocumentById(Number.parseInt(id))
            if (doc) {
                setDocument(doc)

                // Get related documents by tags
                const related = getDocumentsByTag(doc.tags[0])
                    .filter((relDoc) => relDoc.id !== doc.id)
                    .slice(0, 4)
                setRelatedDocuments(related)

                // Check if document is in favorites
                if (user) {
                    const favorites = JSON.parse(localStorage.getItem(`favorites_${user.id}`) || "[]")
                    setIsFavorite(favorites.includes(doc.id))
                }
            } else {
                navigate("/documents")
            }
            setIsLoading(false)
        }
    }, [id, user, navigate])

    const toggleFavorite = () => {
        if (!user) {
            alert("Vui lòng đăng nhập để thêm vào yêu thích")
            return
        }

        if (!document) return

        const favorites = JSON.parse(localStorage.getItem(`favorites_${user.id}`) || "[]")
        const updatedFavorites = isFavorite
            ? favorites.filter((favId: number) => favId !== document.id)
            : [...favorites, document.id]

        localStorage.setItem(`favorites_${user.id}`, JSON.stringify(updatedFavorites))
        setIsFavorite(!isFavorite)
    }

    // const handleDownload = () => {
    //     if (!document) return
    //
    //     try {
    //         // Tạo nội dung file dựa trên loại file
    //         let fileContent = ""
    //         let mimeType = "text/plain"
    //         let fileName = `${document.title}.${document.fileType.toLowerCase()}`
    //
    //         switch (document.fileType.toUpperCase()) {
    //             case "TXT":
    //                 fileContent = `${document.title}\n\nMô tả: ${document.description}\n\nNội dung:\n${document.content?.replace(/<[^>]*>/g, "") || "Nội dung tài liệu..."}\n\nTác giả: ${document.author}\nNgày đăng: ${new Date(document.publishDate).toLocaleDateString("vi-VN")}`
    //                 mimeType = "text/plain"
    //                 break
    //
    //             case "MD":
    //                 fileContent = `# ${document.title}\n\n**Mô tả:** ${document.description}\n\n**Tác giả:** ${document.author}\n\n**Ngày đăng:** ${new Date(document.publishDate).toLocaleDateString("vi-VN")}\n\n## Nội dung\n\n${document.content?.replace(/<[^>]*>/g, "") || "Nội dung tài liệu..."}\n\n---\n\n*Tải từ DevShare - Nền tảng chia sẻ tài liệu IT*`
    //                 mimeType = "text/markdown"
    //                 break
    //
    //             case "HTML":
    //                 fileContent = `<!DOCTYPE html>\n<html>\n<head>\n<title>${document.title}</title>\n<meta charset="UTF-8">\n</head>\n<body>\n<h1>${document.title}</h1>\n<p><strong>Mô tả:</strong> ${document.description}</p>\n<p><strong>Tác giả:</strong> ${document.author}</p>\n<div>${document.content || "Nội dung tài liệu..."}</div>\n</body>\n</html>`
    //                 mimeType = "text/html"
    //                 fileName = `${document.title}.html`
    //                 break
    //
    //             default:
    //                 fileContent = `${document.title}\n\nMô tả: ${document.description}\n\nTác giả: ${document.author}\nNgày đăng: ${new Date(document.publishDate).toLocaleDateString("vi-VN")}\n\nNội dung:\n${document.content?.replace(/<[^>]*>/g, "") || "Nội dung tài liệu..."}\n\n---\nTải từ DevShare`
    //                 mimeType = "text/plain"
    //                 fileName = `${document.title}.txt`
    //         }
    //
    //         // Tạo và download file
    //         const blob = new Blob([fileContent], { type: mimeType })
    //         const url = URL.createObjectURL(blob)
    //         const link = document.createElement("a")
    //
    //         link.href = url
    //         link.download = fileName
    //         link.style.display = "none"
    //
    //         document.body.appendChild(link)
    //         link.click()
    //         document.body.removeChild(link)
    //
    //         // Cleanup
    //         setTimeout(() => URL.revokeObjectURL(url), 100)
    //
    //         // Update download count
    //         setDocument({ ...document, downloads: document.downloads + 1 })
    //
    //         // Show success message
    //         alert(`Đã tải xuống: ${fileName}`)
    //     } catch (error) {
    //         console.error("Download error:", error)
    //         alert("Có lỗi xảy ra khi tải file. Vui lòng thử lại!")
    //     }
    // }
    

    const generateFileContent = (doc: any) => {
        switch (doc.fileType) {
            case "TXT":
            case "MD":
                return `${doc.title}\n\n${doc.description}\n\n${doc.content?.replace(/<[^>]*>/g, "") || "Nội dung tài liệu..."}`
            default:
                return `${doc.title}\n\n${doc.description}\n\nĐây là file ${doc.fileType} được tạo từ DevShare.\nNội dung: ${doc.content?.replace(/<[^>]*>/g, "") || "Nội dung tài liệu..."}`
        }
    }

    const getFileType = (fileType: string) => {
        switch (fileType) {
            case "PDF":
                return "application/pdf"
            case "DOC":
                return "application/msword"
            case "TXT":
                return "text/plain"
            case "MD":
                return "text/markdown"
            default:
                return "text/plain"
        }
    }

    const handleShare = async () => {
        if (!document) return

        const shareData = {
            title: document.title,
            text: document.description,
            url: window.location.href,
        }

        try {
            if (navigator.share) {
                await navigator.share(shareData)
            } else {
                // Fallback: copy to clipboard
                await navigator.clipboard.writeText(window.location.href)
                alert("Link đã được sao chép vào clipboard!")
            }
        } catch (error) {
            console.error("Error sharing:", error)
        }
    }

    if (isLoading) {
        return (
            <div className="document-detail-page">
                <div className="loading-container">
                    <div className="loading-spinner"></div>
                    <p>Đang tải tài liệu...</p>
                </div>
            </div>
        )
    }

    if (!document) {
        return (
            <div className="document-detail-page">
                <div className="error-container">
                    <h2>Không tìm thấy tài liệu</h2>
                    <button onClick={() => navigate("/documents")} className="btn-primary">
                        Quay lại thư viện
                    </button>
                </div>
            </div>
        )
    }

    return (
        <div className="document-detail-page">
            <div className="document-container">
                <div className="document-header">
                    <div className="document-info">
                        <div className="breadcrumb">
              <span onClick={() => navigate("/")} className="breadcrumb-link">
                Trang chủ
              </span>
                            <i className="fas fa-chevron-right"></i>
                            <span onClick={() => navigate("/documents")} className="breadcrumb-link">
                Thư viện tài liệu
              </span>
                            <i className="fas fa-chevron-right"></i>
                            <span className="breadcrumb-current">{document.title}</span>
                        </div>

                        <h1 className="document-title">{document.title}</h1>
                        <p className="document-description">{document.description}</p>

                        <div className="document-meta">
                            <div className="meta-item">
                                <i className="fas fa-user"></i>
                                <span>Tác giả: {document.author}</span>
                            </div>
                            <div className="meta-item">
                                <i className="fas fa-calendar"></i>
                                <span>Ngày đăng: {new Date(document.publishDate).toLocaleDateString("vi-VN")}</span>
                            </div>
                            <div className="meta-item">
                                <i className="fas fa-download"></i>
                                <span>{document.downloads.toLocaleString()} lượt tải</span>
                            </div>
                            <div className="meta-item">
                                <i className="fas fa-star"></i>
                                <span>
                  {document.rating}/5 ({Math.floor(Math.random() * 100) + 50} đánh giá)
                </span>
                            </div>
                        </div>

                        <div className="document-tags">
                            {document.tags.map((tag: string) => (
                                <span key={tag} className="tag" onClick={() => navigate(`/documents?search=${tag}`)}>
                  {tag}
                </span>
                            ))}
                        </div>
                    </div>

                    <div className="document-sidebar">
                        <div className="file-preview">
                            <div className={`file-icon large ${document.iconClass}`}>
                                <i className={document.icon}></i>
                            </div>
                            <div className="file-details">
                                <span className="file-type">{document.fileType}</span>
                                <span className="file-size">{document.fileSize}</span>
                                <span className="file-pages">{document.pages} trang</span>
                            </div>
                        </div>

                        <div className="action-buttons">
                            <button className="btn-primary download-btn" onClick={handleDownload}>
                                <i className="fas fa-download"></i>
                                Tải xuống
                            </button>
                            <button className={`btn-secondary favorite-btn ${isFavorite ? "active" : ""}`} onClick={toggleFavorite}>
                                <i className={`fas fa-heart ${isFavorite ? "filled" : ""}`}></i>
                                {isFavorite ? "Đã yêu thích" : "Yêu thích"}
                            </button>
                            <button className="btn-secondary share-btn" onClick={handleShare}>
                                <i className="fas fa-share-alt"></i>
                                Chia sẻ
                            </button>
                        </div>

                        <div className="author-info">
                            <img src={document.authorAvatar || "/placeholder.svg"} alt={document.author} />
                            <div className="author-details">
                                <h4>{document.author}</h4>
                                <p>Tác giả</p>
                                <button className="btn-outline">Xem hồ sơ</button>
                            </div>
                        </div>
                    </div>
                </div>

                <div className="document-content">
                    <div className="content-tabs">
                        <button
                            className={`tab-btn ${activeTab === "content" ? "active" : ""}`}
                            onClick={() => setActiveTab("content")}
                        >
                            <i className="fas fa-file-alt"></i>
                            Nội dung
                        </button>
                        <button
                            className={`tab-btn ${activeTab === "reviews" ? "active" : ""}`}
                            onClick={() => setActiveTab("reviews")}
                        >
                            <i className="fas fa-star"></i>
                            Đánh giá
                        </button>
                        <button
                            className={`tab-btn ${activeTab === "comments" ? "active" : ""}`}
                            onClick={() => setActiveTab("comments")}
                        >
                            <i className="fas fa-comments"></i>
                            Bình luận
                        </button>
                    </div>

                    <div className="tab-content">
                        {activeTab === "content" && (
                            <div className="content-tab">
                                <div className="content-preview" dangerouslySetInnerHTML={{ __html: document.content }} />
                            </div>
                        )}

                        {activeTab === "reviews" && (
                            <div className="reviews-tab">
                                <div className="reviews-summary">
                                    <div className="rating-overview">
                                        <div className="rating-score">
                                            <span className="score">{document.rating}</span>
                                            <div className="stars">
                                                {[...Array(5)].map((_, i) => (
                                                    <i key={i} className={`fas fa-star ${i < Math.floor(document.rating) ? "filled" : ""}`}></i>
                                                ))}
                                            </div>
                                            <span className="total-reviews">({Math.floor(Math.random() * 100) + 50} đánh giá)</span>
                                        </div>
                                    </div>
                                </div>

                                <div className="reviews-list">
                                    {[1, 2, 3].map((review) => (
                                        <div key={review} className="review-item">
                                            <div className="review-header">
                                                <img src={`/placeholder.svg?height=40&width=40`} alt="User" />
                                                <div className="review-info">
                                                    <h5>Người dùng {review}</h5>
                                                    <div className="review-rating">
                                                        {[...Array(5)].map((_, i) => (
                                                            <i key={i} className={`fas fa-star ${i < 4 ? "filled" : ""}`}></i>
                                                        ))}
                                                    </div>
                                                    <span className="review-date">2 ngày trước</span>
                                                </div>
                                            </div>
                                            <p className="review-text">
                                                Tài liệu rất hữu ích và dễ hiểu. Nội dung được trình bày một cách logic và có nhiều ví dụ thực
                                                tế.
                                            </p>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        )}

                        {activeTab === "comments" && (
                            <div className="comments-tab">
                                <div className="comment-form">
                                    <h4>Để lại bình luận</h4>
                                    <textarea placeholder="Chia sẻ suy nghĩ của bạn về tài liệu này..."></textarea>
                                    <button className="btn-primary">Gửi bình luận</button>
                                </div>

                                <div className="comments-list">
                                    {[1, 2].map((comment) => (
                                        <div key={comment} className="comment-item">
                                            <img src={`/placeholder.svg?height=40&width=40`} alt="User" />
                                            <div className="comment-content">
                                                <div className="comment-header">
                                                    <h5>Người dùng {comment}</h5>
                                                    <span className="comment-date">1 giờ trước</span>
                                                </div>
                                                <p>Cảm ơn tác giả đã chia sẻ tài liệu hữu ích này!</p>
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        )}
                    </div>
                </div>

                {relatedDocuments.length > 0 && (
                    <div className="related-documents">
                        <h3>Tài liệu liên quan</h3>
                        <div className="related-grid">
                            {relatedDocuments.map((relDoc) => (
                                <div key={relDoc.id} className="related-card" onClick={() => navigate(`/document/${relDoc.id}`)}>
                                    <div className={`file-icon ${relDoc.iconClass}`}>
                                        <i className={relDoc.icon}></i>
                                    </div>
                                    <div className="related-info">
                                        <h4>{relDoc.title}</h4>
                                        <p>{relDoc.author}</p>
                                        <div className="related-stats">
                      <span>
                        <i className="fas fa-download"></i> {relDoc.downloads}
                      </span>
                                            <span>
                        <i className="fas fa-star"></i> {relDoc.rating}
                      </span>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                )}
            </div>
        </div>
    )
}

export default DocumentDetailPage
